mapper.select:
	/* dev.hash.hashcord.post.mapper.PostMapper.selectChanelSubScribCnt - 채널 구독자 수 조회 */
    SELECT COUNT(HCS.USER_ID) AS CNT
      FROM HASH_CHANEL_SUBCRIB HCS
     WHERE 1 = 1

     	AND HCS.CHANEL_ID = #{chanelId}

	/* dev.hash.hashcord.post.mapper.PostMapper.selectChanelCategoryList - 채널 카테고리 목록조회 */
    SELECT 	 HCH.CHANEL_NM
           , HCA.CATEGORY_ID
           , HCA.CATEGORY_NM
      FROM HASH_CHANEL HCH
     INNER JOIN HASH_CATEGORY HCA
    		 ON HCH.CHANEL_ID = HCA.CHANEL_ID
     WHERE HCH.CHANEL_DEL_YN = 'N'

     	AND HCH.CHANEL_ID = #{chanelId}


     	AND HCA.CATEGORY_ID = #{categoryId}

/* dev.hash.hashcord.post.mapper.PostMapper.selectPostList - 게시글 목록조회 */ SELECT HCH.CHANEL_NM , HCA.CATEGORY_NM , HP.POST_ID , HP.POST_TITLE , HP.POST_REG_ID , ( SELECT HU.USER_NM FROM HASH_USER HU WHERE HU.USER_ID = HP.POST_REG_ID ) AS POST_REG_NM , DATE(HP.POST_REG_DT) AS POST_REG_DT , HP.POST_MOD_ID , ( SELECT HU.USER_NM FROM HASH_USER HU WHERE HU.USER_ID = HP.POST_MOD_ID ) AS POST_MOD_NM , DATE(HP.POST_MOD_DT) AS POST_MOD_DT , HP.POST_DEL_YN FROM HASH_POST HP INNER JOIN HASH_CATEGORY HCA ON HP.CATEGORY_ID = HCA.CATEGORY_ID INNER JOIN HASH_CHANEL HCH ON HCH.CHANEL_ID = HCA.CHANEL_ID WHERE HP.POST_DEL_YN = 'N'
AND HCH.CHANEL_ID = #{chanelId}
AND HCA.CATEGORY_ID = #{categoryId}
AND HP.POST_TITLE LIKE CONCAT('%', #{postTitle}, '%')
